                    var CustomersByHotelService = $resource('./customers/customers/:customerId/hotel/:hotelId/addStaff/:addStaff', {customerId: '@customerId', hotelId: '@hotelId', addStaff: '@addStaff'}, {'getCustomers': {method: 'GET', isArray: true}});

